import React, {Component} from 'react'
import './App.css';
import UserInput from './UserInterface/UserInput.js'
import UserOutput from './UserInterface/UserOutput.js'

class App extends Component{
  state={
    userName : [
      {name:'Pratik'}, 
      {name:'Raj'}
    ]
  };

  nameHandler = (event) =>{
    this.setState ({
      userName : [
        {name:event.target.value}, 
        {name:'Raj'}
      ]
    })
  }

  render(){
    return(
      <div className = 'App'>
        <UserInput changed={this.nameHandler} userName= {this.state.userName[0].name }/>
        <UserOutput userName = {this.state.userName[0].name}/>
        <UserOutput userName ={this.state.userName[1].name}/>
      </div>
    );
  }
}


export default App;
